import React from "react";
import { Navbar, NavbarBrand, Nav, NavItem, NavLink } from "reactstrap";
import { Link } from "react-router-dom";

import SignOutButton from "./SignOut";
import * as routes from "../constants/routes";

import AuthUserContext from "./AuthUserContext";


const Navigation = () => (
  <AuthUserContext.Consumer>
    {authUser =>
      authUser ? <NavigationAuth userInfo={authUser} /> : <NavigationNonAuth />
    }
  </AuthUserContext.Consumer>
);

class Sample1 extends React.Component{
  constructor(){
      super();
      this.state={
          show:true
      }
  }
  render(){
    return (
      <div className="Sample1">
        <header className="App-header">
          <div>
              {
                  this.state.show? <div><h1>Hide and Show</h1></div> : null
              }
              <button onClick={()=>{this.setState({show:!this.state.show})}}>{ this.state.show? 'Hide' : 'Show'} Div</button>
          </div>
        </header>
      </div>
    );
  }
}


//Toggle Menu: Show & Hide
function myToggleMenu() {
  var x = document.getElementById("myDIV");
  if (x.style.display === "none") {
    x.style.display = "block";
  } else {
    x.style.display = "none";
  }
}

const NavigationNonAuth = () => (
  <React.Fragment>
  <header class="cd-header">
			<div class="header-wrapper">
				<div class="logo-wrap">
					<a href="/" class="cursor-link animsition-link"><img
							src="https://ivang-design.com/alke/img/logo.png" alt="" /></a>
				</div>
				<div class="nav-but-wrap">
					<button id="menu-toggle" class="menu-icon" onclick={myToggleMenu}>
						<span class="menu-icon__line menu-icon__line-left"></span>
						<span class="menu-icon__line"></span>
						<span class="menu-icon__line menu-icon__line-left"></span>
					</button>
				</div>
			</div>
		</header>

		<div id="myDIV">
		<p style={{color:"white"}}>Test</p>
		</div>
    </React.Fragment>
);


export default Navigation;

const NavigationAuth = ({ userInfo }) => (

  <header class="cd-header">
    <div class="header-wrapper">
      <div class="logo-wrap">
        <Link to={routes.LANDING} ><img
          src="https://ivang-design.com/alke/img/logo@2x.png" alt="" /></Link>


      </div>
      <div class="nav-but-wrap">
        <div class="cursor-link animsition-link">
          <Link to={routes.HOME}>My Projects</Link>
          {userInfo.providerData[0].providerId === "facebook.com" ? null : (

            <Link to={routes.ACCOUNT}>Account</Link>

          )}
          <SignOutButton />
        </div>
      </div>
    </div>
  </header>





);

