import React, { Component } from "react";

import * as routes from "../constants/routes";
import { Link } from "react-router-dom";

import withAuthorization from "./withAuthorization";
import { db } from "../firebase";

import Project1 from "./Project1";
import Project2 from "./Project2";
import Project3 from "./Project3";
import Project4 from "./Project4";
import Project5 from "./Project5";
import Project6 from "./Project6";

import './MainStyle.css';
import './HeroBgs.css';
import {mouseProject1, mouseProject2, mouseProject3, mouseProject4, mouseProject5, mouseProject6 } from './ProjectItems';

class HomePage extends Component {
  state = {
    users: null,
    username: "",
    loading: true
  };

  componentDidMount() {
    db.onceGetUsers().then(res => {
      this.setState({
        users: res.val()
      });
    });

    const { loggedUser } = this.props;
    db.doGetAnUnser(loggedUser.uid).then(res => {
      this.setState({
        username: res.val().username,
        loading: false
      });
    });
  }


  render() {
    const { users, username, loading } = this.state;
    // console.log("dasdf", this.props.loggedUser);
    return (

      <React.Fragment>
		  <div class="section full-height over-hide background-dark-2">
			<div class="case-study-name-title">case studies</div>
			<ul class="case-study-wrapper">
				<li class="case-study-name">                            	
					<span>Reconstype</span> <a href="project.html" class="cursor-link animsition-link">explore</a>
				</li>
				<li class="case-study-name">                                         	
					<span>Kresios</span> <a href="project.html" class="cursor-link animsition-link">explore</a>
				</li>
				<li class="case-study-name">                                        	
					<span>Cioccolati</span> <a href="project.html" class="cursor-link animsition-link">explore</a>
				</li>
				<li class="case-study-name">                                         	
					<span>Maybach</span> <a href="project.html" class="cursor-link animsition-link">explore</a>
				</li>
				<li class="case-study-name">                                           	
					<span>Anwis Stuttgart</span> <a href="project.html" class="cursor-link animsition-link">explore</a>
				</li>
				<li class="case-study-name">                                         	
					<span>Brand Center</span> <a href="project.html" class="cursor-link animsition-link">explore</a>
				</li>
			</ul>
			<ul class="case-study-images">
				<li>
					<div class="img-hero-background" style={{backgroundImage:"url('https://ivang-design.com/alke/img/1.jpg')"}}></div> 
					<div class="dark-over-hero"></div>  
					<div class="hero-number-back">01</div> 
					<div class="hero-number">01</div> 
					<div class="hero-number-fixed">06</div> 
					<div class="case-study-title">graphic Design, typography, interaction</div> 
				</li>
				<li>
					<div class="img-hero-background" style={{backgroundImage:"url('https://ivang-design.com/alke/img/2.jpg')"}}></div> 
					<div class="hero-number-back">02</div> 
					<div class="hero-number">02</div>  
					<div class="case-study-title">advertising, art direction</div> 
				</li>
				<li>
					<div class="img-hero-background" style={{backgroundImage:"url('https://ivang-design.com/alke/img/3.jpg')"}}></div>
					<div class="hero-number-back">03</div>  
					<div class="hero-number">03</div> 
					<div class="case-study-title">art direction, photography, retouching</div> 
				</li>
				<li>
					<div class="img-hero-background" style={{backgroundImage:"url('https://ivang-design.com/alke/img/4.jpg')"}}></div> 
					<div class="hero-number-back">04</div> 
					<div class="hero-number">04</div> 
					<div class="case-study-title">photography, advertising, fine arts</div> 
				</li>
				<li>
					<div class="img-hero-background" style={{backgroundImage:"url('https://ivang-design.com/alke/img/5.jpg')"}}></div> 
					<div class="hero-number-back">05</div> 
					<div class="hero-number">05</div> 
					<div class="case-study-title">architecture, exhibition design</div> 
				</li>
				<li>
					<div class="img-hero-background" style={{backgroundImage:"url('https://ivang-design.com/alke/img/6.jpg')"}}></div> 
					<div class="hero-number-back">06</div> 
					<div class="hero-number">06</div> 
					<div class="case-study-title">interior design</div> 
				</li>
			</ul>	
			<div class="social-wrap">
				follow us
				<ul>
					<li><a href="#" class="cursor-link">fb</a></li>
					<li><a href="#" class="cursor-link">tw</a></li>
					<li><a href="#" class="cursor-link">be</a></li>
				</ul>
			</div>	
		</div>


	<div class="cursor cursor-shadow"></div>
	<div class="cursor cursor-dot"></div>
	  </React.Fragment>
    );
  }
}

const UserList = ({ users }) => (
  <div>
    {console.log("users", users)}
    <h2>List of Usernames of Users</h2>
    <p>(Saved on Sign Up in Firebase Database)</p>

    {Object.keys(users).map(key => (
      <div key={key}>{users[key].username}</div>
    ))}
  </div>
);

const authCondition = authUser => !!authUser;

export default withAuthorization(authCondition)(HomePage); //grants authorization to open endpoint if an user is signed in
