import React, { Component } from "react";

import * as routes from "../constants/routes";
import { Link } from "react-router-dom";

import withAuthorization from "./withAuthorization";
import { db } from "../firebase";

import Project1 from "./Project1";
import Project2 from "./Project2";
import Project3 from "./Project3";
import Project4 from "./Project4";
import Project5 from "./Project5";
import Project6 from "./Project6";

class HomePage extends Component {
  state = {
    users: null,
    username: "",
    loading: true
  };

  componentDidMount() {
    db.onceGetUsers().then(res => {
      this.setState({
        users: res.val()
      });
    });

    const { loggedUser } = this.props;
    db.doGetAnUnser(loggedUser.uid).then(res => {
      this.setState({
        username: res.val().username,
        loading: false
      });
    });
  }

  render() {
    const { users, username, loading } = this.state;
    // console.log("dasdf", this.props.loggedUser);
    return (
      <div>
        <h1>Recent Projects</h1>
        {!loading && <p className="centered">Hello {username}!</p>}



        <div class="card-deck">
          <div class="card">
            <Link to={routes.PROJECT1} component={Project1}>
              <img src="https://dummyimage.com/300.png/09f/fff" class="card-img-top" alt="..." />
            </Link>
            <div class="card-body">
              <h5 class="card-title">The Buzz by Messe Frankfurt Middle East</h5>
              <p class="card-text">This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
              <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
            </div>
          </div>
          <div class="card">
            <Link to={routes.PROJECT2} component={Project2}>
              <img src="https://dummyimage.com/300.png/09f/fff" class="card-img-top" alt="..." />
            </Link>
            <div class="card-body">
              <h5 class="card-title">Intersec, Intersec Saudi Arabia, Intersec Awards</h5>
              <p class="card-text">This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
              <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
            </div>
          </div>
          <div class="card">
            <Link to={routes.PROJECT1} component={Project1}>
              <img src="https://dummyimage.com/300.png/09f/fff" class="card-img-top" alt="..." />
            </Link>
            <div class="card-body">
              <h5 class="card-title">The Buzz by Messe Frankfurt Middle East</h5>
              <p class="card-text">This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
              <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
            </div>
          </div>
          <div class="card">
            <Link to={routes.PROJECT1} component={Project1}>
              <img src="https://dummyimage.com/300.png/09f/fff" class="card-img-top" alt="..." />
            </Link>
            <div class="card-body">
              <h5 class="card-title">The Buzz by Messe Frankfurt Middle East</h5>
              <p class="card-text">This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
              <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
            </div>
          </div>
          <div class="card">
            <Link to={routes.PROJECT1} component={Project1}>
              <img src="https://dummyimage.com/300.png/09f/fff" class="card-img-top" alt="..." />
            </Link>
            <div class="card-body">
              <h5 class="card-title">The Buzz by Messe Frankfurt Middle East</h5>
              <p class="card-text">This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
              <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
            </div>
          </div>
          <div class="card">
            <Link to={routes.PROJECT1} component={Project1}>
              <img src="https://dummyimage.com/300.png/09f/fff" class="card-img-top" alt="..." />
            </Link>
            <div class="card-body">
              <h5 class="card-title">The Buzz by Messe Frankfurt Middle East</h5>
              <p class="card-text">This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
              <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
            </div>
          </div>

        </div>

        <ul>

          <li><Link to={routes.PROJECT2} component={Project2}>Intersec: Online Exhibition Marketing</Link></li>
          <li><Link to={routes.PROJECT3} component={Project3}>CODE Challenge: Volunteer 365</Link></li>
          <li><Link to={routes.PROJECT4} component={Project4}>AstroLabs FULL STACK Project: Superhero Portal</Link></li>
          <li><Link to={routes.PROJECT5} component={Project5}>Maxicare Locator App</Link></li>
          <li><Link to={routes.PROJECT6} component={Project1}>People in Art: Personal Illustrations</Link></li>
        </ul>
        {/* {!!users && <UserList users={users} />} */}
      </div>
    );
  }
}

const UserList = ({ users }) => (
  <div>
    {console.log("users", users)}
    <h2>List of Usernames of Users</h2>
    <p>(Saved on Sign Up in Firebase Database)</p>

    {Object.keys(users).map(key => (
      <div key={key}>{users[key].username}</div>
    ))}
  </div>
);

const authCondition = authUser => !!authUser;

export default withAuthorization(authCondition)(HomePage); //grants authorization to open endpoint if an user is signed in
