var nodes = document.querySelectorAll(".letters");
  [...nodes].forEach(node => {
    let text = node.innerText;
    [...text].forEach((txt, index) => {
      if(txt !== " "){
        let innernode = document.createElement("span");
        innernode.style.left = (Math.random()*75) + "vw";
        innernode.style.animationDelay = index + "s";
        innernode.innerText = txt;
        node.appendChild(innernode);
      }
    })
  })
