import React, { Component } from "react";

import withAuthorization from "./withAuthorization";
import { db } from "../firebase";

class Project1 extends Component {
  state = {
    users: null,
    username: "",
    loading: true
  };

  componentDidMount() {
    // db.onceGetUsers().then(res => {
    //   this.setState({
    //     users: res.val()
    //   });
    // });

    const { loggedUser } = this.props;
    db.doGetAnUnser(loggedUser.uid).then(res => {
      this.setState({
        username: res.val().username,
        loading: false
      });
    });
  }

  render() {
    return (
      <div class="container">

      <h1 class="my-4">The Buzz
        <small> by Messe Frankfurt Middle East</small>
      </h1>
    
      <div class="row">
    
        <div class="col-md-8">
          <img class="img-fluid" src="http://placehold.it/750x500" alt=""/>
        </div>
    
        <div class="col-md-4">
          <h3 class="my-3">The Buzz Lockdown</h3>
          <p>The last 6-7 weeks have been unprecedented; we’ve been forced to adapt to new situations and procedures in almost a blink of an eye; we’ve had to abandon our plans and reorganise almost every part of our daily lives.  We’ve never experienced anything like this before, and we hope we’ll never experience it again.</p>
          <h3 class="my-3">Contribution</h3>
          <ul>
            <li>Creative Direction</li>
            <li>Creative Execution: Web, Video</li>
            <li>Less Tok, More Action (Tiktok Challenge)</li>
            <li>Work-from-home Challenge</li>
            <li>E-mail Promotion and Data Sorting</li>
          </ul>
        </div>
    
      </div>
      <hr />

      <h3 class="my-4">Previous Editions</h3>
    
      <div class="row">
    
        <div class="col-md-4 col-sm-6 mb-4">
          <a href="#">
                <img class="img-fluid" src="http://placehold.it/500x300" alt=""/>
              </a>
              <p>Winter Edition 2019: From Print to Digital</p>
        </div>
    
        <div class="col-md-4 col-sm-6 mb-4">
          <a href="#">
                <img class="img-fluid" src="http://placehold.it/500x300" alt=""/>
              </a>
        </div>
    
        <div class="col-md-4 col-sm-6 mb-4">
          <a href="#">
                <img class="img-fluid" src="http://placehold.it/500x300" alt=""/>
              </a>
        </div>
    

    
      </div>

    
    </div>

    );
  }
}

const authCondition = authUser => !!authUser;

export default withAuthorization(authCondition)(Project1); //grants authorization to open endpoint if an user is signed in
