import React, { Component } from "react";

import withAuthorization from "./withAuthorization";
import { db } from "../firebase";

class Project1 extends Component {
  state = {
    users: null,
    username: "",
    loading: true
  };

  componentDidMount() {
    // db.onceGetUsers().then(res => {
    //   this.setState({
    //     users: res.val()
    //   });
    // });

    const { loggedUser } = this.props;
    db.doGetAnUnser(loggedUser.uid).then(res => {
      this.setState({
        username: res.val().username,
        loading: false
      });
    });
  }

  render() {
    return (
      <div class="container">

      <h1 class="my-4">The Buzz
        <small>by Messe Frankfurt Middle East</small>
      </h1>
    
      <div class="row">
    
        <div class="col-md-8">
          <img class="img-fluid" src="http://placehold.it/750x500" alt=""/>
        </div>
    
        <div class="col-md-4">
          <h3 class="my-3">The Buzz</h3>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam viverra euismod odio, gravida pellentesque urna varius vitae. Sed dui lorem, adipiscing in adipiscing et, interdum nec metus. Mauris ultricies, justo eu convallis placerat, felis enim.</p>
          <h3 class="my-3">Project Details</h3>
          <ul>
            <li>Lorem Ipsum</li>
            <li>Dolor Sit Amet</li>
            <li>Consectetur</li>
            <li>Adipiscing Elit</li>
          </ul>
        </div>
    
      </div>

      <h3 class="my-4">Related Projects</h3>
    
      <div class="row">
    
        <div class="col-md-3 col-sm-6 mb-4">
          <a href="#">
                <img class="img-fluid" src="http://placehold.it/500x300" alt=""/>
              </a>
        </div>
    
        <div class="col-md-3 col-sm-6 mb-4">
          <a href="#">
                <img class="img-fluid" src="http://placehold.it/500x300" alt=""/>
              </a>
        </div>
    
        <div class="col-md-3 col-sm-6 mb-4">
          <a href="#">
                <img class="img-fluid" src="http://placehold.it/500x300" alt=""/>
              </a>
        </div>
    
        <div class="col-md-3 col-sm-6 mb-4">
          <a href="#">
                <img class="img-fluid" src="http://placehold.it/500x300" alt="" />
              </a>
        </div>
    
      </div>

    
    </div>

    );
  }
}

const authCondition = authUser => !!authUser;

export default withAuthorization(authCondition)(Project1); //grants authorization to open endpoint if an user is signed in
