import React, { Component } from "react";

import withAuthorization from "./withAuthorization";
import { db } from "../firebase";

class Project1 extends Component {
  state = {
    users: null,
    username: "",
    loading: true
  };

  componentDidMount() {
    // db.onceGetUsers().then(res => {
    //   this.setState({
    //     users: res.val()
    //   });
    // });

    const { loggedUser } = this.props;
    db.doGetAnUnser(loggedUser.uid).then(res => {
      this.setState({
        username: res.val().username,
        loading: false
      });
    });
  }

  render() {
    const { users, username, loading } = this.state;
    // console.log("dasdf", this.props.loggedUser);
    return (
      <div>
        <h1>The buzz by Messe Frankfurt Middle East</h1>
        {!loading && <p className="centered">Hello {username}!</p>}

        <p style={{ marginTop: "80px" }}>
          A brief documentation of some of my favourite projects.
        </p>
        <ul>
          <li>The Buzz by Messe Frankfurt Middle East</li>
          <li>Intersec: Online Exhibition Marketing</li>
          <li>AstroLabs FULL STACK Project: Superhero Portal</li>
          <li>CODE Challenge: Volunteer 365</li>
          <li>Maxicare Locator App</li>
          <li>People in Art: Personal Illustrations</li>
        </ul>
        {/* {!!users && <UserList users={users} />} */}
      </div>
    );
  }
}

const authCondition = authUser => !!authUser;

export default withAuthorization(authCondition)(Project1); //grants authorization to open endpoint if an user is signed in
