import React, { Component } from "react";

import * as routes from "../constants/routes";
import { Link } from "react-router-dom";

import withAuthorization from "./withAuthorization";
import { db } from "../firebase";

class HomePage extends Component {
  state = {
    users: null,
    username: "",
    loading: true
  };

  componentDidMount() {
    db.onceGetUsers().then(res => {
      this.setState({
        users: res.val()
      });
    });

    const { loggedUser } = this.props;
    db.doGetAnUnser(loggedUser.uid).then(res => {
      this.setState({
        username: res.val().username,
        loading: false
      });
    });
  }

  render() {
    const { users, username, loading } = this.state;
    // console.log("dasdf", this.props.loggedUser);
    return (
      <div>
        <h1>Projects</h1>
        {!loading && <p className="centered">Hello {username}!</p>}

        <p style={{ marginTop: "80px" }}>
          A brief documentation of some of my favourite projects.
        </p>


        <ul>
          <li><Link to={routes.PROJECT1}>The Buzz by Messe Frankfurt Middle East</Link></li>
          <li><Link to={routes.PROJECT2}>Intersec: Online Exhibition Marketing</Link></li>
          <li><Link to={routes.PROJECT3}>CODE Challenge: Volunteer 365</Link></li>
          <li><Link to={routes.PROJECT4}>AstroLabs FULL STACK Project: Superhero Portal</Link></li>
          <li><Link to={routes.PROJECT5}>Maxicare Locator App</Link></li>
          <li><Link to={routes.PROJECT6}>People in Art: Personal Illustrations</Link></li>
        </ul>
        {/* {!!users && <UserList users={users} />} */}
      </div>
    );
  }
}

const UserList = ({ users }) => (
  <div>
    {console.log("users", users)}
    <h2>List of Usernames of Users</h2>
    <p>(Saved on Sign Up in Firebase Database)</p>

    {Object.keys(users).map(key => (
      <div key={key}>{users[key].username}</div>
    ))}
  </div>
);

const authCondition = authUser => !!authUser;

export default withAuthorization(authCondition)(HomePage); //grants authorization to open endpoint if an user is signed in
